pragma solidity 0.5.17;

import "./BlenderCore.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "./proxy/Initializable.sol";

contract ERC20Blender is Initializable, BlenderCore {
    address public token;

    // DO NOT implement a constructor because this is an upgradable logic.
    // Use the initialize function as a constructor.
    constructor() public {}

    function initialize(
        WVerifier _withdrawVerifier,
        RVerifier _rewardVerifier,
        uint256 _d_denomination,
        uint32 _merkleTreeHeight,
        uint32 _blockCount,
        address _operator,
        address _blnd,
        address _token,
        uint256 _firstStageReward,
        uint256 _secondStageReward,
        uint256 _thirdStageReward,
        uint256 _firstStageDepositors,
        uint256 _secondStageDepositors
    ) public initializer {
        // call the initialize function of the parent contract (the constructor of the parent contract)
        BlenderCore._initialize(
            _withdrawVerifier,
            _rewardVerifier,
            _d_denomination,
            _merkleTreeHeight,
            _blockCount,
            _operator,
            _blnd,
            _firstStageReward,
            _secondStageReward,
            _thirdStageReward,
            _firstStageDepositors,
            _secondStageDepositors
        );
        // constructor logic
        token = _token;
    }

    function _processDeposit() internal {
        require(
            msg.value == 0,
            "ETH value is supposed to be 0 for ERC20 instance"
        );
        SafeERC20.safeTransferFrom(
            IERC20(token),
            msg.sender,
            address(this),
            d_denomination
        );
        // equal to deposit
    }

    function _processWithdraw(
        address payable _recipient,
        address payable _relayer,
        uint256 _fee,
        uint256 _refund
    ) internal {
        require(
            msg.value == _refund,
            "Incorrect refund amount received by the contract"
        );

        SafeERC20.safeTransfer(
            IERC20(token),
            _recipient,
            d_denomination - _fee
        );
        if (_fee > 0) {
            SafeERC20.safeTransfer(IERC20(token), _relayer, _fee);
        }

        if (_refund > 0) {
            (bool success, ) = _recipient.call.value(_refund)("");
            if (!success) {
                // let's return _refund back to the relayer
                _relayer.transfer(_refund);
            }
        }
    }
}
